<span class="line"><span style="color:#88C0D0;">      9DC8</span><span style="color:#B48EAD;"> 5822</span><span style="color:#A3BE8C;"> 9FC7</span><span style="color:#A3BE8C;"> DD38</span><span style="color:#A3BE8C;"> 854A</span><span style="color:#A3BE8C;">  E2D8</span><span style="color:#A3BE8C;"> 8D81</span><span style="color:#A3BE8C;"> 803C</span><span style="color:#A3BE8C;"> 0EBF</span><span style="color:#A3BE8C;"> CD88</span></span>
