import{_ as s,c as n,o as a,a6 as l}from"./chunks/framework.BDSt01_6.js";const u=JSON.parse('{"title":"自制操作系统log","description":"","frontmatter":{"title":"自制操作系统log","date":"2019-09-15T21:42:42.000Z","toc":true,"tags":["OS","操作系统","自制","记录"]},"headers":[],"relativePath":"posts/zh/自制操作系统log.md","filePath":"posts/zh/自制操作系统log.md"}'),p={name:"posts/zh/自制操作系统log.md"},e=l(`<h2 id="参考书" tabindex="-1">参考书 <a class="header-anchor" href="#参考书" aria-label="Permalink to &quot;参考书&quot;">​</a></h2><ul><li>《三十天自制操作系统（川合秀实）》</li><li>《ORANGE&#39;S：一个操作系统的实现》</li></ul><h2 id="写在前面的" tabindex="-1">写在前面的 <a class="header-anchor" href="#写在前面的" aria-label="Permalink to &quot;写在前面的&quot;">​</a></h2><p>本记录不展示原版书籍的内容，仅作为自己历程的记录和总结。两本书各有所长，根据自己的喜好选择和实践。书籍中的内容根据自己的喜好、习惯，有一定的自己理解和改变！本记录默认对二进制、十六进制和基础硬件等都有一定的了解！</p><h2 id="制作启动区-512字节" tabindex="-1">制作启动区(512字节) <a class="header-anchor" href="#制作启动区-512字节" aria-label="Permalink to &quot;制作启动区(512字节)&quot;">​</a></h2><p>从开始我对两本书都是懵逼的，结合两本书涉及的启动盘代码总结一点东西</p><div class="language-asm line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">asm</span><pre class="shiki nord vp-code"><code><span class="line"><span style="color:#D8DEE9FF;">ORG </span><span style="color:#B48EAD;">0x7c00</span><span style="color:#616E88;">                  ; 程序加载0x7c00</span></span>
<span class="line"></span>
<span class="line"><span style="color:#616E88;">; FAT12格式软盘代码位</span></span>
<span class="line"><span style="color:#616E88;">; (--省略--)</span></span>
<span class="line"></span>
<span class="line"><span style="color:#81A1C1;">JMP</span><span style="color:#D8DEE9FF;"> entry</span></span>
<span class="line"><span style="color:#81A1C1;">DB</span><span style="color:#B48EAD;"> 0x90</span></span>
<span class="line"></span>
<span class="line"><span style="color:#88C0D0;">entry</span><span style="color:#ECEFF4;">:</span></span>
<span class="line"><span style="color:#81A1C1;">        MOV</span><span style="color:#81A1C1;"> AX</span><span style="color:#D8DEE9FF;">, </span><span style="color:#B48EAD;">0</span><span style="color:#616E88;">           ; 初始化寄存器</span></span>
<span class="line"><span style="color:#81A1C1;">        MOV</span><span style="color:#81A1C1;"> SS</span><span style="color:#D8DEE9FF;">, </span><span style="color:#81A1C1;">AX</span></span>
<span class="line"><span style="color:#81A1C1;">        MOV</span><span style="color:#81A1C1;"> SP</span><span style="color:#D8DEE9FF;">, </span><span style="color:#B48EAD;">0x7c00</span></span>
<span class="line"><span style="color:#81A1C1;">        MOV</span><span style="color:#81A1C1;"> DS</span><span style="color:#D8DEE9FF;">, </span><span style="color:#81A1C1;">AX</span></span>
<span class="line"><span style="color:#81A1C1;">        MOV</span><span style="color:#81A1C1;"> ES</span><span style="color:#D8DEE9FF;">, </span><span style="color:#81A1C1;">AX</span></span>
<span class="line"></span>
<span class="line"><span style="color:#81A1C1;">        MOV</span><span style="color:#81A1C1;"> SI</span><span style="color:#D8DEE9FF;">, msg</span></span>
<span class="line"></span>
<span class="line"><span style="color:#88C0D0;">putloop</span><span style="color:#ECEFF4;">:</span></span>
<span class="line"><span style="color:#81A1C1;">        MOV</span><span style="color:#81A1C1;"> AL</span><span style="color:#D8DEE9FF;">, [</span><span style="color:#81A1C1;">SI</span><span style="color:#D8DEE9FF;">]        </span><span style="color:#616E88;">; []表示内存中</span></span>
<span class="line"><span style="color:#81A1C1;">        ADD</span><span style="color:#81A1C1;"> SI</span><span style="color:#D8DEE9FF;">, </span><span style="color:#B48EAD;">1</span><span style="color:#616E88;">           ; SI加一</span></span>
<span class="line"><span style="color:#81A1C1;">        CMP</span><span style="color:#81A1C1;"> AL</span><span style="color:#D8DEE9FF;">, </span><span style="color:#B48EAD;">0</span></span>
<span class="line"></span>
<span class="line"><span style="color:#81A1C1;">        JE</span><span style="color:#D8DEE9FF;"> fin              </span><span style="color:#616E88;">; jump if equal</span></span>
<span class="line"><span style="color:#81A1C1;">        MOV</span><span style="color:#81A1C1;"> AH</span><span style="color:#D8DEE9FF;">, </span><span style="color:#B48EAD;">0x0e</span><span style="color:#616E88;">        ; 显示一个文字</span></span>
<span class="line"><span style="color:#81A1C1;">        MOV</span><span style="color:#81A1C1;"> BX</span><span style="color:#D8DEE9FF;">, </span><span style="color:#B48EAD;">15</span><span style="color:#616E88;">          ; 指定字符颜色</span></span>
<span class="line"><span style="color:#81A1C1;">        INT</span><span style="color:#B48EAD;"> 0x10</span><span style="color:#616E88;">            ; 调用显卡BIOS，INT软件中断指令</span></span>
<span class="line"><span style="color:#81A1C1;">        JMP</span><span style="color:#D8DEE9FF;"> putloop</span></span>
<span class="line"></span>
<span class="line"><span style="color:#88C0D0;">fin</span><span style="color:#ECEFF4;">:</span></span>
<span class="line"><span style="color:#81A1C1;">        HLT</span><span style="color:#616E88;">                 ; 让CPU停止等待指令</span></span>
<span class="line"><span style="color:#81A1C1;">        JMP</span><span style="color:#D8DEE9FF;"> fin             </span><span style="color:#616E88;">; 无限循环</span></span>
<span class="line"></span>
<span class="line"><span style="color:#88C0D0;">msg</span><span style="color:#ECEFF4;">:</span></span>
<span class="line"><span style="color:#81A1C1;">        DB</span><span style="color:#B48EAD;"> 0x0a</span><span style="color:#D8DEE9FF;">, </span><span style="color:#B48EAD;">0x0a</span><span style="color:#616E88;">       ; 换行</span></span>
<span class="line"><span style="color:#81A1C1;">        DB</span><span style="color:#D8DEE9FF;"> &quot;hello, world&quot;</span></span>
<span class="line"><span style="color:#81A1C1;">        DB</span><span style="color:#B48EAD;"> 0x0a</span></span>
<span class="line"><span style="color:#81A1C1;">        DB</span><span style="color:#B48EAD;"> 0</span></span>
<span class="line"></span>
<span class="line"><span style="color:#81A1C1;">        RESB</span><span style="color:#B48EAD;"> 0x7dfe</span><span style="color:#D8DEE9FF;">-$       </span><span style="color:#616E88;">; 填写0x00直到0x001fe</span></span>
<span class="line"><span style="color:#81A1C1;">        DB</span><span style="color:#B48EAD;"> 0x55</span><span style="color:#D8DEE9FF;">, </span><span style="color:#B48EAD;">0xaa</span><span style="color:#616E88;">       ; 结束</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br><span class="line-number">4</span><br><span class="line-number">5</span><br><span class="line-number">6</span><br><span class="line-number">7</span><br><span class="line-number">8</span><br><span class="line-number">9</span><br><span class="line-number">10</span><br><span class="line-number">11</span><br><span class="line-number">12</span><br><span class="line-number">13</span><br><span class="line-number">14</span><br><span class="line-number">15</span><br><span class="line-number">16</span><br><span class="line-number">17</span><br><span class="line-number">18</span><br><span class="line-number">19</span><br><span class="line-number">20</span><br><span class="line-number">21</span><br><span class="line-number">22</span><br><span class="line-number">23</span><br><span class="line-number">24</span><br><span class="line-number">25</span><br><span class="line-number">26</span><br><span class="line-number">27</span><br><span class="line-number">28</span><br><span class="line-number">29</span><br><span class="line-number">30</span><br><span class="line-number">31</span><br><span class="line-number">32</span><br><span class="line-number">33</span><br><span class="line-number">34</span><br><span class="line-number">35</span><br><span class="line-number">36</span><br><span class="line-number">37</span><br><span class="line-number">38</span><br><span class="line-number">39</span><br><span class="line-number">40</span><br></div></div><blockquote><p>注意：30天那个书制作启动盘的部分完全可以看看就行。。。代码就是上面的，就是helloos.nas节选那个末尾加上了两行，写满512字节和结束标志！</p></blockquote><p>代码直接看<a href="https://github.com/yourtion/30dayMakeOS" target="_blank" rel="noreferrer">这个库</a></p>`,9),o=[e];function r(c,t,i,y,b,E){return a(),n("div",null,o)}const A=s(p,[["render",r]]);export{u as __pageData,A as default};
